({data:{},init:function(){this.getData("seattle")},getData:async function(t){const e=await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${t}&appid=2ce4c90e826af0f3a3f83a9bf293c425`,{mode:"cors"}),n=await e.json();this.data.location=n.name,this.data.tempC=this.getTempC(n.main.temp),this.data.tempF=this.getTempF(n.main.temp),this.data.feelsLikeC=this.getTempC(n.main.feels_like),this.data.feelsLikeF=this.getTempF(n.main.feels_like),this.data.humidity=n.main.humidity,this.data.windSpeed=n.wind.speed,this.data.windDirection=n.wind.deg,this.data.report=n.weather[0].description,this.render(),this.addListener()},getTempF:function(t){return Math.round(1.8*(t-273.15)+32)},getTempC:function(t){return Math.round(t-273.15)},getWindDirection:function(){},render:function(){const t=document.getElementById("location"),e=document.getElementById("tempF"),n=document.getElementById("tempC"),i=document.getElementById("feelsLikeF"),a=document.getElementById("feelsLikeC"),d=document.getElementById("report"),o=document.getElementById("windSpeed"),s=document.getElementById("windDirection"),m=document.getElementById("humidity");t.textContent=this.data.location,e.textContent=this.data.tempF,n.textContent=this.data.tempC,i.textContent=this.data.feelsLikeF,a.textContent=this.data.feelsLikeC,m.textContent=this.data.humidity,o.textContent=this.data.windSpeed,s.textContent=this.data.windDirection,d.textContent=this.data.report},addListener:function(){const t=document.querySelector("form");t.addEventListener("submit",(e=>{e.preventDefault();const n=new FormData(t).get("search");this.getData(n),t.reset()}))}}).init();